  create_view "un_paid_receivables", sql_definition: <<-SQL
      SELECT  
    CASE 
        WHEN d .[TRXTYPETXT] = 1 THEN 'Invoice'
        WHEN d .[TRXTYPETXT] = 2 THEN 'Debit Note'
        WHEN d .[TRXTYPETXT] = 3 THEN 'Credit Note'
        WHEN d .[TRXTYPETXT] = 4 THEN 'Interest'
        WHEN d .[TRXTYPETXT] = 5 THEN 'Unapplied Cash'
        WHEN d .[TRXTYPETXT] = 10 THEN 'Prepayment'
        WHEN d .[TRXTYPETXT] = 11 THEN 'Receipt'
        WHEN d .[TRXTYPETXT] = 19 THEN 'Refund' 
        ELSE 'Unknown' 
    END AS DOCTYPE,
    d.IDCUST, NAMECUST, IDINVC, IDORDERNBR, d.IDGRP, DATEINVC, DATEDUE, DATEASOF,
    d.CODESLSP1, d.CODESLSP2, d.CODESLSP3, d.CODESLSP4, d.CODESLSP5 IDSHIPNBR, d.CODETERR, CODECTRY,
    DATELSTACT, DATELSTSTM, d.CODETERM, d.CODECURN, AMTINVCHC, AMTDUEHC, ISNULL
    (
        (
            SELECT TOP(1) AMTDUEHC
            FROM ARRTB AS t
            WHERE DATEDIFF(DAY, (CONVERT(DATE, NULLIF (CONVERT(VARCHAR(8), CONVERT(INT, d.DATEDUE)), 0))), CONVERT(DATE, GETDATE(),0) ) > t.DUEDAYS
            GROUP BY DUEDAYS
            ORDER BY DUEDAYS DESC
        ), 0
    ) AS INAMTOVER, ISNULL
    (
        (
            SELECT TOP (1) SUM(PCTDUE) AS Expr1
            FROM dbo.ARRTB AS t
            WHERE (CODETERM = d.CODETERM) AND (DATEADD(DAY, DUEDAYS, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, d.DATEINVC)), 0))) < GETDATE())
            GROUP BY CODETERM
        ), 0
    ) AS TERMPERTOVER, ISNULL
    (
        (
            SELECT TOP (1) 
            CASE 
                WHEN DATEDIFF(DAY, (CONVERT(DATE, NULLIF (CONVERT(VARCHAR(8), CONVERT(INT, d.DATEDUE)), 0))), CONVERT(DATE, GETDATE(),0)) < 0
                THEN DATEDIFF(DAY, (CONVERT(DATE, NULLIF (CONVERT(VARCHAR(8), CONVERT(INT, d.DATEDUE)), 0))), CONVERT(DATE, GETDATE(),0)) * -1
            ELSE
                DATEDIFF(DAY, (CONVERT(DATE, NULLIF (CONVERT(VARCHAR(8), CONVERT(INT, d.DATEDUE)), 0))), CONVERT(DATE, GETDATE(),0))
            END 
            FROM dbo.ARRTB AS t
            WHERE (CODETERM = d.CODETERM)
            ORDER BY CODETERM, CNTPAYM
        ), 0
    ) AS TERMDAYSOVER

  FROM dbo.AROBL AS d
  JOIN ARCUS ON d.IDCUST = ARCUS.IDCUST
  WHERE (SWPAID = 0)
  ;
  SQL
  create_view "item_moving_averages", sql_definition: <<-SQL
      SELECT
    ITEMNO, LOCATION
    --Average quantity sales
    ,TOWYEARSQTY
    ,FOURQUARTERQTY
    ,THIRDQUARTERQTY
    ,SECONDQUARTERQTY
    ,FIRSTQUARTERQTY
    --Average amunt sales
    ,TOWYEARSAMT
    ,FOURQUARTERAMT
    ,THIRDQUARTERAMT
    ,SECONDQUARTERAMT
    ,FIRSTQUARTERAMT
    -- Sales Counts
    ,TOWYEARSCOUNT
    ,FOURQUARTERCOUNT
    ,THIRDQUARTERCOUNT
    ,SECONDQUARTERCOUNT
    ,FIRSTQUARTERCOUNT
  FROM
    (
      SELECT
        ITEMNO
        ,LOCATION
        ,[Fiscal Year]
        ,[Fiscal Period]
        ,[Sales Quantity]
        ,[Sales Count]
        -- Sales Averages
        ,AVG([Sales Quantity]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 23 PRECEDING AND 0 FOLLOWING)  AS TOWYEARSQTY
        ,AVG([Sales Quantity]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 11 PRECEDING AND 0 FOLLOWING)  AS FOURQUARTERQTY
         ,AVG([Sales Quantity]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 8 PRECEDING AND 0 FOLLOWING)  AS THIRDQUARTERQTY
        ,AVG([Sales Quantity]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 5 PRECEDING AND 0 FOLLOWING)  AS SECONDQUARTERQTY
        ,AVG([Sales Quantity]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 2 PRECEDING AND 0 FOLLOWING)  AS FIRSTQUARTERQTY
        -- Sales Amount
        ,AVG([Sales Amount]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 23 PRECEDING AND 0 FOLLOWING)  AS TOWYEARSAMT
        ,AVG([Sales Amount]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 11 PRECEDING AND 0 FOLLOWING)  AS FOURQUARTERAMT
        ,AVG([Sales Amount]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 8 PRECEDING AND 0 FOLLOWING)  AS THIRDQUARTERAMT
        ,AVG([Sales Amount]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 5 PRECEDING AND 0 FOLLOWING)  AS SECONDQUARTERAMT
        ,AVG([Sales Amount]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 2 PRECEDING AND 0 FOLLOWING)  AS FIRSTQUARTERAMT
        -- Sales Count
        ,SUM([Sales Count]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 24 PRECEDING AND CURRENT ROW)  AS TOWYEARSCOUNT
        ,SUM([Sales Count]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 11 PRECEDING AND CURRENT ROW)  AS FOURQUARTERCOUNT
        ,SUM([Sales Count]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 8 PRECEDING AND CURRENT ROW)  AS THIRDQUARTERCOUNT
        ,SUM([Sales Count]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 5 PRECEDING AND CURRENT ROW)  AS SECONDQUARTERCOUNT
        ,SUM([Sales Count]) OVER (PARTITION BY ITEMNO, LOCATION ORDER BY [Fiscal Year], [Fiscal Period] ROWS BETWEEN 2 PRECEDING AND CURRENT ROW)  AS FIRSTQUARTERCOUNT
    FROM
    (
      SELECT DISTINCT
        il.ITEMNO
        ,il.LOCATION
        ,CAST(ff.FSCYEAR AS INT) AS "Fiscal Year"
        ,ff.Period AS "Fiscal Period"
        ,ISNULL((SELECT iss.SALESQTY FROM dbo.ICSTATI iss
          WHERE iss.ITEMNO = il.ITEMNO
          AND iss.LOCATION = il.LOCATION
          AND iss.[YEAR] = ff.FSCYEAR
          AND iss.PERIOD = ff.Period),0) AS "Sales Quantity"
        ,ISNULL((SELECT iss.SALESAMT FROM dbo.ICSTATI iss
          WHERE iss.ITEMNO = il.ITEMNO
          AND iss.LOCATION = il.LOCATION
          AND iss.[YEAR] = ff.FSCYEAR
          AND iss.PERIOD = ff.Period),0) AS "Sales Amount"
        ,ISNULL((SELECT iss.SALESCOUNT FROM dbo.ICSTATI iss
          WHERE iss.ITEMNO = il.ITEMNO
          AND iss.LOCATION = il.LOCATION
          AND iss.[YEAR] = ff.FSCYEAR
          AND iss.PERIOD = ff.Period),0) AS "Sales Count"
      FROM dbo.ICILOC il
      RIGHT JOIN
        (
          SELECT f.FSCYEAR, 1 AS "Period" from [dbo].[CSFSC] f WHERE CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,f.[ENDDATE1])),0)) < GETDATE()
          UNION SELECT f.FSCYEAR, 2 AS "Period" from [dbo].[CSFSC] f WHERE CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,f.[ENDDATE2])),0)) < GETDATE()
          UNION SELECT f.FSCYEAR, 3 AS "Period" from [dbo].[CSFSC] f WHERE CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,f.[ENDDATE3])),0)) < GETDATE()
          UNION SELECT f.FSCYEAR, 4 AS "Period" from [dbo].[CSFSC] f WHERE CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,f.[ENDDATE4])),0)) < GETDATE()
          UNION SELECT f.FSCYEAR, 5 AS "Period" from [dbo].[CSFSC] f WHERE CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,f.[ENDDATE5])),0)) < GETDATE()
          UNION SELECT f.FSCYEAR, 6 AS "Period" from [dbo].[CSFSC] f WHERE CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,f.[ENDDATE6])),0)) < GETDATE()
          UNION SELECT f.FSCYEAR, 7 AS "Period" from [dbo].[CSFSC] f WHERE CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,f.[ENDDATE7])),0)) < GETDATE()
          UNION SELECT f.FSCYEAR, 8 AS "Period" from [dbo].[CSFSC] f WHERE CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,f.[ENDDATE8])),0)) < GETDATE()
          UNION SELECT f.FSCYEAR, 9 AS "Period" from [dbo].[CSFSC] f WHERE CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,f.[ENDDATE9])),0)) < GETDATE()
          UNION SELECT f.FSCYEAR, 10 AS "Period" from [dbo].[CSFSC] f WHERE CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,f.[ENDDATE10])),0)) < GETDATE()
          UNION SELECT f.FSCYEAR, 11 AS "Period" from [dbo].[CSFSC] f WHERE CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,f.[ENDDATE11])),0)) < GETDATE()
          UNION SELECT f.FSCYEAR, 12 AS "Period" from [dbo].[CSFSC]f  WHERE CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,f.[ENDDATE12])),0)) < GETDATE()
        ) ff ON ff.FSCYEAR >= YEAR(DATEADD(MONTH, -24, GETDATE()))
        WHERE
          il.LOCATION IN ('1','1L','10','10L','4D','5L')
          AND il.USED = 1
          AND il.ACTIVE = 1) q
    ) s
  WHERE [Fiscal Year] = YEAR(GETDATE()) AND [Fiscal Period] = MONTH(GETDATE()) -1
  ;
  SQL
  create_view "items_rankings", sql_definition: <<-SQL
      SELECT
    q.ITEM AS item,
    CASE
      WHEN SUM(SALES_AMOUNT) OVER (ORDER BY SALES_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(SALES_AMOUNT) OVER (),0) <= 0.7 THEN 'A'
      WHEN SUM(SALES_AMOUNT) OVER (ORDER BY SALES_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(SALES_AMOUNT) OVER (),0) <= 0.9 THEN 'B'
      ELSE 'C'
    END AS abc_sales_rank,
    DENSE_RANK() OVER (PARTITION BY 1,2 ORDER BY SALES_AMOUNT DESC) AS dense_sales_rank,
    CASE
      WHEN SUM(COST_AMOUNT) OVER (ORDER BY COST_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(COST_AMOUNT) OVER (),0) <= 0.7 THEN 'A'
      WHEN SUM(COST_AMOUNT) OVER (ORDER BY COST_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(COST_AMOUNT) OVER (),0) <= 0.9 THEN 'B'
      ELSE 'C'
    END AS abc_cost_rank,
    DENSE_RANK() OVER (PARTITION BY 1,2 ORDER BY COST_AMOUNT DESC) AS dense_cost_rank,
    CASE
      WHEN SUM(MARGIN_AMOUNT) OVER (ORDER BY MARGIN_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(MARGIN_AMOUNT) OVER (),0) <= 0.7 THEN 'A'
      WHEN SUM(MARGIN_AMOUNT) OVER (ORDER BY MARGIN_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(MARGIN_AMOUNT) OVER (),0) <= 0.9 THEN 'B'
      ELSE 'C'
    END AS abc_margin_rank,
    DENSE_RANK() OVER (PARTITION BY 1,2 ORDER BY MARGIN_AMOUNT DESC) AS dense_margin_rank
  FROM
  (
    SELECT
      RTRIM(ITEM) AS ITEM,
      SUM(FAMTSALES) AS SALES_AMOUNT,
      SUM(FCSTSALES) AS COST_AMOUNT,
      SUM(FAMTSALES) - SUM(FCSTSALES) AS MARGIN_AMOUNT
    FROM OESHDT
    WHERE DATEADD(MONTH, -36, GETDATE()) <= CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,TRANDATE)),0))
    GROUP BY AUDTORG, ITEM
  )q
  ;
  SQL
  create_view "items_ranking_locations", sql_definition: <<-SQL
      SELECT
  q.LOCATION AS location, q.ITEM AS item,
  CASE
    WHEN SUM(q.SALES_AMOUNT) OVER (PARTITION BY q.LOCATION ORDER BY q.SALES_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(q.SALES_AMOUNT) OVER (PARTITION BY q.LOCATION),0) <= 0.7 THEN 'A'
    WHEN SUM(q.SALES_AMOUNT) OVER (PARTITION BY q.LOCATION ORDER BY q.SALES_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(q.SALES_AMOUNT) OVER (PARTITION BY q.LOCATION),0) <= 0.9 THEN 'B'
    ELSE 'C'
  END AS abc_sales_rank,
  DENSE_RANK() OVER (PARTITION BY q.LOCATION ORDER BY q.SALES_AMOUNT DESC) AS dense_sales_rank,
  CASE
    WHEN SUM(q.COST_AMOUNT) OVER (PARTITION BY q.LOCATION ORDER BY q.COST_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(q.COST_AMOUNT) OVER (PARTITION BY q.LOCATION),0) <= 0.7 THEN 'A'
    WHEN SUM(q.COST_AMOUNT) OVER (PARTITION BY q.LOCATION ORDER BY q.COST_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(q.COST_AMOUNT) OVER (PARTITION BY q.LOCATION ),0) <= 0.9 THEN 'B'
    ELSE 'C'
  END AS abc_cost_rank,
  DENSE_RANK() OVER (PARTITION BY q.LOCATION ORDER BY q.COST_AMOUNT DESC) AS dense_cost_rank,
  CASE
    WHEN SUM(q.MARGIN_AMOUNT) OVER (PARTITION BY q.LOCATION ORDER BY q.MARGIN_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(q.MARGIN_AMOUNT) OVER (PARTITION BY q.LOCATION),0) <= 0.7 THEN 'A'
    WHEN SUM(q.MARGIN_AMOUNT) OVER (PARTITION BY q.LOCATION ORDER BY q.MARGIN_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(q.MARGIN_AMOUNT) OVER (PARTITION BY q.LOCATION),0) <= 0.9 THEN 'B'
    ELSE 'C'
  END AS abc_margin_rank,
  DENSE_RANK() OVER (PARTITION BY q.LOCATION ORDER BY q.MARGIN_AMOUNT DESC) AS dense_margin_rank
  FROM
  ( SELECT
    RTRIM(shd.[ITEM]) AS ITEM, shd.[LOCATION] AS LOCATION, SUM(shd.[FAMTSALES]) AS SALES_AMOUNT,
    SUM(shd.[FCSTSALES]) AS COST_AMOUNT ,SUM(shd.[FAMTSALES]) - SUM(shd.[FCSTSALES]) AS MARGIN_AMOUNT
    FROM [OESHDT] shd
    WHERE DATEADD(MONTH, -36, GETDATE()) <= CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,shd.[TRANDATE])),0))
    GROUP BY shd.[ITEM], shd.[LOCATION]
  )q
  ;
  SQL
  create_view "items_location_details", sql_definition: <<-SQL
      SELECT
    k.ITEMNO AS ITEMNO, i.FMTITEMNO, i.[DESC], k.LOCATION AS LOCATION, il.QTYONHAND AS QTYONHAND, c.CATEGORY,
    il.QTYCOMMIT AS QTYCOMMIT, il.QTYSALORDR AS QTYSALORDR, il.QTYONORDER AS QTYONORDER, ir.abc_sales_rank,
    il.RECENTCOST, (il.QTYONHAND*il.RECENTCOST) AS MRAMOUNT, il.TOTALCOST,
    ma.TOWYEARSQTY, ma.FOURQUARTERQTY, ma.THIRDQUARTERQTY, ma.SECONDQUARTERQTY, ma.FIRSTQUARTERQTY,
    ma.TOWYEARSAMT, ma.FOURQUARTERAMT, ma.THIRDQUARTERAMT, ma.SECONDQUARTERAMT, ma.FIRSTQUARTERAMT, 
    c.[DESC] AS CDESC, ((ma.TOWYEARSQTY+ma.FOURQUARTERQTY+ma.THIRDQUARTERQTY+ma.SECONDQUARTERQTY+ma.FIRSTQUARTERQTY)/4) AS AVERAGE
    ,ISNULL(CASE (ma.FOURQUARTERQTY+ma.THIRDQUARTERQTY+ma.SECONDQUARTERQTY+ma.FIRSTQUARTERQTY)
        WHEN 0 THEN 0.00
        ELSE ((QTYONHAND - QTYCOMMIT)/((ma.FOURQUARTERQTY+ma.THIRDQUARTERQTY+ma.SECONDQUARTERQTY+ma.FIRSTQUARTERQTY)/4))
    END,0.0) AS COVERAGE
    ,ISNULL(CASE k.LOCATION
        WHEN '1'    THEN (SELECT p.QTYONORDER FROM dbo.ICILOC p WHERE p.ITEMNO = k.ITEMNO AND p.LOCATION = '99')
        WHEN '1L'   THEN (SELECT p.QTYONORDER FROM dbo.ICILOC p WHERE p.ITEMNO = k.ITEMNO AND p.LOCATION = '991L')
        WHEN '10'   THEN (SELECT p.QTYONORDER FROM dbo.ICILOC p WHERE p.ITEMNO = k.ITEMNO AND p.LOCATION = '99E')
        WHEN '10L'  THEN (SELECT p.QTYONORDER FROM dbo.ICILOC p WHERE p.ITEMNO = k.ITEMNO AND p.LOCATION = '99L')
        WHEN '4D'   THEN (SELECT p.QTYONORDER FROM dbo.ICILOC p WHERE p.ITEMNO = k.ITEMNO AND p.LOCATION = '994D')
        WHEN '5L'   THEN (SELECT p.QTYONORDER FROM dbo.ICILOC p WHERE p.ITEMNO = k.ITEMNO AND p.LOCATION = '995L')
        ELSE 0.00
    END,0.0) AS PRODUCTION
    ,ISNULL((SELECT SUM(ICTRED.QTYREQ) FROM dbo.ICTREH
        JOIN dbo.ICTRED ON ICTRED.TRANFENSEQ = ICTREH.TRANFENSEQ
        WHERE ICTREH.STATUS <> 20
        AND ITEMNO = FMTITEMNO
        AND ICTRED.FROMLOC = k.LOCATION
        GROUP BY ICTRED.ITEMNO, ICTRED.FROMLOC),0.0) AS TRANSFOUT
        
    ,ISNULL((SELECT SUM(ICTRED.QTYREQ) FROM dbo.ICTREH
        JOIN dbo.ICTRED ON ICTRED.TRANFENSEQ = ICTREH.TRANFENSEQ
        WHERE ICTREH.STATUS <> 20
        AND ITEMNO = FMTITEMNO
        AND ICTRED.TOLOC = k.LOCATION
        GROUP BY ICTRED.ITEMNO, ICTRED.TOLOC),0.0) AS TRANSFIN
    ,MAX(CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,il.[LASTSHIPDT])),0))) AS LASTSHIPDT
    ,MAX(CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,il.[LASTRCPTDT])),0))) AS LASTRCPTDT
        -- Optional Fields
        ,(
            SELECT LTRIM(RTRIM(VALUE))
            FROM ICITEMO AS o
            WHERE (k.ITEMNO = o.ITEMNO) AND (OPTFIELD = 'CATEGORY')
        ) AS OPTFCAT
        ,(
            SELECT LTRIM(RTRIM(VALUE))
            FROM ICITEMO AS o
            WHERE (k.ITEMNO = o.ITEMNO) AND (OPTFIELD = 'DEPT')
        ) AS OPTFDEP
        ,(
            SELECT LTRIM(RTRIM(VALUE))
            FROM ICITEMO AS o
            WHERE (k.ITEMNO = o.ITEMNO) AND (OPTFIELD = 'PRODUCT')
        ) AS OPTFPROD
        ,(
            SELECT LTRIM(RTRIM(VALUE))
            FROM ICITEMO AS o
            WHERE (k.ITEMNO = o.ITEMNO) AND (OPTFIELD = 'TYPE')
        ) AS OPTFTYPE
        ,(
            SELECT LTRIM(RTRIM(VALUE))
            FROM ICITEMO AS o
            WHERE (k.ITEMNO = o.ITEMNO) AND (OPTFIELD = 'SUBFAMILY')
        ) AS SUBFAMILY
        -- Vendors
    ,ISNULL((
        SELECT VENDNUM
        FROM ICITMV iv
        WHERE k.ITEMNO = iv.ITEMNO AND VENDTYPE = 1
    ), NULL) AS VENDOR1
    ,ISNULL((
        SELECT VENDNUM
        FROM ICITMV iv
        WHERE k.ITEMNO = iv.ITEMNO AND VENDTYPE = 2
    ), NULL) AS VENDOR2
    ,ISNULL((
        SELECT VENDNUM
        FROM ICITMV iv
        WHERE k.ITEMNO = iv.ITEMNO AND VENDTYPE = 3
    ), NULL) AS VENDOR3
    ,ISNULL((
        SELECT VENDNUM
        FROM ICITMV iv
        WHERE k.ITEMNO = iv.ITEMNO AND VENDTYPE = 4
    ),NULL) AS VENDOR4
    ,ISNULL((
        SELECT VENDNUM
        FROM ICITMV iv
        WHERE k.ITEMNO = iv.ITEMNO AND VENDTYPE = 5
    ), NULL) AS VENDOR5
    ,ISNULL((
        SELECT VENDNUM
        FROM ICITMV iv
        WHERE k.ITEMNO = iv.ITEMNO AND VENDTYPE = 6
    ), NULL) AS VENDOR6
  FROM
    (
      SELECT iss.ITEMNO, iss.LOCATION, iss.YEAR, iss.PERIOD
      FROM dbo.ICSTATI iss
      --WHERE iss.YEAR > YEAR(DATEADD(MONTH, -36, GETDATE()))
      --AND iss.LOCATION IN ('1','1L','10','10L','4D','5L')
    ) k
  JOIN dbo.ICILOC il ON k.ITEMNO = il.ITEMNO AND k.LOCATION = il.LOCATION
  JOIN dbo.ICITEM i ON k.ITEMNO = i.ITEMNO
  JOIN dbo.ICCATG c ON i.CATEGORY = c.CATEGORY
  LEFT JOIN item_moving_averages ma ON il.ITEMNO = ma.ITEMNO AND il.[LOCATION] = ma.[LOCATION]
  LEFT JOIN items_ranking_locations ir ON il.ITEMNO = ir.item AND il.LOCATION = ir.[location]
  GROUP BY
    k.ITEMNO, k.LOCATION, il.QTYONHAND ,il.QTYCOMMIT, il.QTYSALORDR, il.QTYONORDER, il.RECENTCOST, il.TOTALCOST,
    i.FMTITEMNO, i.[DESC], ir.abc_sales_rank, ma.TOWYEARSQTY ,ma.FOURQUARTERQTY,
    ma.THIRDQUARTERQTY, ma.SECONDQUARTERQTY, ma.FIRSTQUARTERQTY, c.CATEGORY, c.[DESC],
    ma.TOWYEARSAMT, ma.FOURQUARTERAMT, ma.THIRDQUARTERAMT, ma.SECONDQUARTERAMT, ma.FIRSTQUARTERAMT
  ;
  SQL
  create_view "fulfillments", sql_definition: <<-SQL
      SELECT
    TOP (100) PERCENT dbo.OEORDD.ITEM, dbo.ICITEM.[DESC], dbo.OEORDH.ORDUNIQ, dbo.OEORDH.ORDNUMBER, dbo.OEORDD.LOCATION, 
    dbo.OEORDH.CUSTOMER, dbo.OEORDH.BILNAME AS CUSTNAME, dbo.OEORDH.SALESPER1 AS SALESREP, dbo.OEORDH.ORDDATE, dbo.OEORDD.QTYORDERED, 
    dbo.OEORDD.QTYBACKORD, dbo.OEORDD.QTYCOMMIT, dbo.ICILOC.QTYONHAND - dbo.ICILOC.QTYCOMMIT AS QTYAVAILABLE, 
    dbo.OEORDD.UNITPRICE * dbo.OEORDD.QTYBACKORD AS AMOUNT, dbo.OEORDH.CUSTGROUP
  FROM dbo.OEORDH 
  INNER JOIN dbo.OEORDD ON dbo.OEORDD.ORDUNIQ = dbo.OEORDH.ORDUNIQ
  INNER JOIN dbo.ICILOC ON REPLACE(dbo.OEORDD.ITEM, '-', '') = dbo.ICILOC.ITEMNO 
    AND dbo.OEORDD.QTYBACKORD < dbo.ICILOC.QTYONHAND - dbo.ICILOC.QTYCOMMIT 
    AND dbo.OEORDD.LOCATION = dbo.ICILOC.LOCATION 
  INNER JOIN dbo.ICITEM ON dbo.ICILOC.ITEMNO = dbo.ICITEM.ITEMNO
  WHERE (dbo.OEORDH.TYPE <> 4)
    AND (dbo.OEORDD.QTYBACKORD - dbo.OEORDD.QTYCOMMIT > 0)
    AND (dbo.OEORDD.COMPLETE = 0)
  ORDER BY dbo.OEORDH.ORDDATE
  ;
  SQL
  create_view "items_moving_ave_details", sql_definition: <<-SQL
      SELECT
    iof.ITEMNO, iof.FMTITEMNO, iof.[DESC],
    iof.CATEGORY, iof.CATDESC,
    ma.LOCATION, abc_sales_rank AS SALESRANK, dense_sales_rank AS DENSESALESRANK,
    ICILOC.QTYONHAND, ICILOC.QTYSALORDR, ICILOC.QTYONORDER, ICILOC.QTYCOMMIT,
    ma.TOWYEARSQTY, ma.FOURQUARTERQTY,
    ma.THIRDQUARTERQTY, ma.SECONDQUARTERQTY,
    ma.FIRSTQUARTERQTY
  FROM item_moving_averages ma
  JOIN item_optional_fields iof ON ma.ITEMNO = iof.ITEMNO COLLATE DATABASE_DEFAULT
  JOIN ICILOC ON ma.ITEMNO = ICILOC.ITEMNO AND ma.LOCATION = ICILOC.[LOCATION] COLLATE DATABASE_DEFAULT
  LEFT JOIN item_ranks ON item = ma.ITEMNO COLLATE DATABASE_DEFAULT
  SQL
  create_view "SalesMarkerPivot", sql_definition: <<-SQL
      SELECT  CUSTOMER AS id, NAMECUST AS customer_name, street, city, state, zip, country, 
          latitude, longitude, [2013], [2014], [2015], [2016], [2017], [2018]
  FROM (
          SELECT  CUSTOMER, YR, SUM(FAMTSALES-FRETSALES) AS NETSALES
          FROM OESHDT
          WHERE YR IN (2013, 2014, 2015, 2016, 2017, 2018)
          GROUP BY CUSTOMER, YR
      ) d PIVOT (SUM(d.NETSALES) FOR YR IN ([2013], [2014], [2015], [2016], [2017], [2018])) AS NETSALES
  JOIN addresses ON CUSTOMER = uniq
  JOIN ARCUS ON CUSTOMER = IDCUST
  WHERE SWACTV = 1
  SQL
  create_view "on_hand_pivot", sql_definition: <<-SQL
      SELECT
    ITEMNO, [1] AS Loc1, [1L] AS Loc1L, [10L] AS Loc10L, [10] AS Loc10, [5L] AS Loc5L, 
    [4D] AS Loc4D, [2D] AS Loc2D, [2M] AS Loc2M, [10M] AS Loc10M, [9] AS Loc9, [12] AS Loc12
  FROM 
    (
        SELECT ITEMNO, LOCATION, QTYONHAND
        FROM  dbo.ICILOC
        WHERE LOCATION IN ('1', '1L', '10L', '10', '5L', '4D', '2D', '2M', '10M', '9', '12')
    ) d PIVOT (SUM(QTYONHAND) FOR LOCATION IN ([1], [1L], [10L], [10], [5L], [4D], [2D], [2M], [10M], [9], [12])) AS on_hand
  ;
  SQL
  create_view "order_action_report", sql_definition: <<-SQL
      SELECT
    od.ORDUNIQ, oh.ORDNUMBER, oh.CUSTOMER,
    oh.BILNAME, ARCUS.CODECTRY, oh.SALESPER1,
    oh.CUSTGROUP, oh.ORDDATE, od.ITEM,
    od.[DESC], od.QTYBACKORD, od.QTYCOMMIT,
    od.LOCATION, od.UNITPRICE, od.UNITCOST, 
    od.MOSTREC, od.AVGCOST, ohp.Loc1,
    ohp.Loc1L, ohp.Loc10L, ohp.Loc10,
    ohp.Loc5L, ohp.Loc4D, ohp.Loc2D,
    ohp.Loc2M, ohp.Loc10M, ohp.Loc9,
    ohp.Loc12, oh.TYPE
  FROM OEORDD od
  INNER JOIN OEORDH oh ON oh.ORDUNIQ = od.ORDUNIQ
  INNER JOIN on_hand_pivot ohp ON ohp.ITEMNO = REPLACE(od.ITEM, '-', '')
  INNER JOIN ARCUS ON oh.CUSTOMER = ARCUS.IDCUST
  WHERE (od.COMPLETE = 0)
  ;
  SQL
  create_view "triggers", sql_definition: <<-SQL
      SELECT
    sys.sysobjects.id, sys.sysobjects.name AS trigger_name,
    USER_NAME(sys.sysobjects.uid) AS trigger_owner, s.name AS table_schema,
    OBJECT_NAME(sys.sysobjects.parent_obj) AS table_name, 
    OBJECTPROPERTY(sys.sysobjects.id, 'ExecIsUpdateTrigger') AS isupdate,
    OBJECTPROPERTY(sys.sysobjects.id, 'ExecIsDeleteTrigger') AS isdelete,
    OBJECTPROPERTY(sys.sysobjects.id, 'ExecIsInsertTrigger') AS isinsert,
    OBJECTPROPERTY(sys.sysobjects.id, 'ExecIsAfterTrigger') AS isafter,
    OBJECTPROPERTY(sys.sysobjects.id, 'ExecIsInsteadOfTrigger') AS isinsteadof,
    OBJECTPROPERTY(sys.sysobjects.id, 'ExecIsTriggerDisabled') AS disabled,
    OBJECT_DEFINITION(sys.sysobjects.id) AS trigger_definition
  FROM sys.sysobjects
  INNER JOIN sys.sysusers ON sys.sysobjects.uid = sys.sysusers.uid
  INNER JOIN sys.tables AS t ON sys.sysobjects.parent_obj = t.object_id
  INNER JOIN sys.schemas AS s ON t.schema_id = s.schema_id
  WHERE (sys.sysobjects.type = 'TR')
  ;
  SQL
  create_view "sales_analysis_details", sql_definition: <<-SQL
      SELECT
    LTRIM(RTRIM(cus.IDCUST)) AS IDCUST, LTRIM(RTRIM(cus.NAMECUST)) AS NAMECUST,  LTRIM(RTRIM(cus.CODESLSP1)) AS CODESLSP1,
    LTRIM(RTRIM(cus.CODESLSP2)) AS CODESLSP2, LTRIM(RTRIM(cus.CODESLSP3)) AS CODESLSP3, LTRIM(RTRIM(cus.IDGRP)) AS IDGRP,
    LTRIM(RTRIM(cus.IDNATACCT)) AS IDNATACCT, LTRIM(RTRIM(cus.CODETERR)) AS CODETERR, LTRIM(RTRIM(cus.NAMECITY)) AS NAMECITY,
    LTRIM(RTRIM(cus.CODESTTE)) AS CODESTTE, LTRIM(RTRIM(cus.CODECTRY)) AS CODECTRY, LTRIM(RTRIM(i.ITEMNO)) AS ITEMNO, 
    LTRIM(RTRIM(i.FMTITEMNO)) AS FMTITEMNO, LTRIM(RTRIM(i.[DESC])) AS [DESC],
    (
        SELECT LTRIM(RTRIM(VALUE))
        FROM dbo.ICITEMO AS o
        WHERE (i.ITEMNO = ITEMNO) AND (OPTFIELD = 'CATEGORY')
    ) AS OPTFCAT,
    (
        SELECT LTRIM(RTRIM(VALUE))
        FROM dbo.ICITEMO AS o
        WHERE (i.ITEMNO = ITEMNO) AND (OPTFIELD = 'DEPT')
    ) AS OPTFDEP,
    (
        SELECT LTRIM(RTRIM(VALUE))
        FROM dbo.ICITEMO AS o
        WHERE (i.ITEMNO = ITEMNO) AND (OPTFIELD = 'PRODUCT')
    ) AS OPTFPROD,
    (
        SELECT LTRIM(RTRIM(VALUE))
        FROM dbo.ICITEMO AS o
        WHERE (i.ITEMNO = ITEMNO) AND (OPTFIELD = 'TYPE')
    ) AS OPTFTYPE,
    (
        SELECT LTRIM(RTRIM(VALUE))
        FROM dbo.ICITEMO AS o
        WHERE (i.ITEMNO = ITEMNO) AND (OPTFIELD = 'COLOR')
    ) AS OPTFCOL,
    (
        SELECT LTRIM(RTRIM(VALUE))
        FROM dbo.ICITEMO AS o
        WHERE (i.ITEMNO = ITEMNO) AND (OPTFIELD = 'SUBFAMILY')
    ) AS SUBFAMILY,
    (
        SELECT LTRIM(RTRIM(VALUE))
        FROM dbo.ICITEMO AS o
        WHERE (i.ITEMNO = ITEMNO) AND (OPTFIELD = 'FAMILY')
    ) AS FAMILY,
    LTRIM(RTRIM(i.CATEGORY)) AS CATEGORY, LTRIM(RTRIM(c.[DESC])) AS CATDESC,  LTRIM(RTRIM(sb.SALESPER)) AS SALESPER,
    LTRIM(RTRIM(sb.TERRITORY)) AS TERRITORY, LTRIM(RTRIM(sb.LOCATION)) AS LOCATION, LTRIM(RTRIM(i.COMMODIM)) AS COMMODIM,
    sb.YR, sb.PERIOD, sb.TRANDATE, sb.QTYSOLD, sb.FAMTSALES, sb.FCSTSALES, sb.FRETSALES, (sb.FAMTSALES - sb.FRETSALES) AS NETSALES,
    (sb.FAMTSALES - sb.FRETSALES - sb.FCSTSALES) AS MARAMOUNT,
    ISNULL((sb.FAMTSALES - sb.FRETSALES) - sb.FCSTSALES / NULLIF (sb.FAMTSALES - sb.FRETSALES, 0), 0) AS MARPERCENT,

    CASE
      WHEN DATEPART(dy, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, TRANDATE)), 0))) <= DATEPART(dy, GETDATE())
      THEN QTYSOLD
      ELSE 0
    END AS YTDQTYSOLD,

    CASE
      WHEN DATEPART(dy, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, [TRANDATE])), 0))) <= DATEPART(dy, GETDATE())
      THEN FAMTSALES
      ELSE 0
    END AS YTDFAMTSALES, 
                    
    CASE
      WHEN DATEPART(dy, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, [TRANDATE])), 0))) <= DATEPART(dy, GETDATE()) 
      THEN FCSTSALES
      ELSE 0
    END AS YTDFCSTSALES,

    CASE
      WHEN DATEPART(dy, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, [TRANDATE])), 0))) <= DATEPART(dy, GETDATE())
      THEN FRETSALES
      ELSE 0
    END AS YTDFRETSALES,

    CASE WHEN DATEPART(dy, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, [TRANDATE])), 0))) <= DATEPART(dy, GETDATE())
      THEN (FAMTSALES - FRETSALES)
      ELSE 0
    END AS YTDNETSALES,

    CASE WHEN DATEPART(dy, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, [TRANDATE])), 0))) <= DATEPART(dy, GETDATE())
      THEN (FAMTSALES - FRETSALES - FCSTSALES) 
      ELSE 0
    END AS YTDGROSMARGIN

      -- Vendors
      ,ISNULL((
              SELECT LTRIM(RTRIM(VENDNUM))
              FROM ICITMV iv
              WHERE i.ITEMNO = iv.ITEMNO AND VENDTYPE = 1
      ), NULL) AS VENDOR1
      ,ISNULL((
              SELECT LTRIM(RTRIM(VENDNUM))
              FROM ICITMV iv
              WHERE i.ITEMNO = iv.ITEMNO AND VENDTYPE = 2
      ), NULL) AS VENDOR2
      ,ISNULL((
              SELECT LTRIM(RTRIM(VENDNUM))
              FROM ICITMV iv
              WHERE i.ITEMNO = iv.ITEMNO AND VENDTYPE = 3
      ), NULL) AS VENDOR3
      ,ISNULL((
              SELECT LTRIM(RTRIM(VENDNUM))
              FROM ICITMV iv
              WHERE i.ITEMNO = iv.ITEMNO AND VENDTYPE = 4
      ),NULL) AS VENDOR4
      ,ISNULL((
              SELECT LTRIM(RTRIM(VENDNUM))
              FROM ICITMV iv
              WHERE i.ITEMNO = iv.ITEMNO AND VENDTYPE = 5
      ), NULL) AS VENDOR5
      ,ISNULL((
              SELECT LTRIM(RTRIM(VENDNUM))
              FROM ICITMV iv
              WHERE i.ITEMNO = iv.ITEMNO AND VENDTYPE = 6
      ), NULL) AS VENDOR6

    FROM dbo.OESHDT AS sb 
    LEFT OUTER JOIN dbo.ICITEM AS i ON i.ITEMNO = sb.ITEM
    LEFT OUTER JOIN dbo.ICCATG AS c ON i.AUDTORG = c.AUDTORG AND i.CATEGORY = c.CATEGORY
    LEFT OUTER JOIN dbo.ARCUS AS cus ON cus.IDCUST = sb.CUSTOMER
  ;
  SQL
  create_view "sales_history_details", sql_definition: <<-SQL
      SELECT
    CUSTOMER, ITEM, YR, PERIOD, TRANDATE, SALESPER, TERRITORY, CATEGORY, LOCATION,
    QTYSOLD, FAMTSALES, FCSTSALES, FRETSALES, FAMTSALES - FRETSALES AS NETSALES,
    FAMTSALES - FRETSALES - FCSTSALES AS MARAMOUNT,
    ISNULL((FAMTSALES - FRETSALES) - FCSTSALES / NULLIF (FAMTSALES - FRETSALES, 0), 0) AS MARPERCENT,
    CASE 
      WHEN DATEPART(dy, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, TRANDATE)), 0))) <= DATEPART(dy, GETDATE())
      THEN QTYSOLD
      ELSE 0 
    END AS YTDQTYSOLD,
    CASE 
      WHEN DATEPART(dy, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, [TRANDATE])), 0))) <= DATEPART(dy, GETDATE())
      THEN FAMTSALES 
      ELSE 0 
    END AS YTDFAMTSALES,
    CASE 
      WHEN DATEPART(dy, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, [TRANDATE])), 0))) <= DATEPART(dy, GETDATE()) 
      THEN FCSTSALES
      ELSE 0 
    END AS YTDFCSTSALES,
    CASE 
      WHEN DATEPART(dy, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, [TRANDATE])), 0))) <= DATEPART(dy, GETDATE())
      THEN FRETSALES
      ELSE 0
    END AS YTDFRETSALES,
    CASE 
      WHEN DATEPART(dy, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, [TRANDATE])), 0))) <= DATEPART(dy, GETDATE())
      THEN (FAMTSALES - FRETSALES)
      ELSE 0
    END AS YTDNETSALES,
    CASE
      WHEN DATEPART(dy, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, [TRANDATE])), 0))) <= DATEPART(dy, GETDATE())
      THEN (FAMTSALES - FRETSALES - FCSTSALES) 
      ELSE 0
    END AS YTDGROSMARGIN
  FROM dbo.OESHDT
  WHERE (YR >= YEAR(DATEADD(MONTH, - 36, GETDATE())))
  ;
  SQL
  create_view "sales_history_summary", sql_definition: <<-SQL
      SELECT
    CUSTOMER, ITEM, YR, PERIOD, SALESPER, TERRITORY, CATEGORY, LOCATION,
    SUM(QTYSOLD) AS QTYSOLD, SUM(FAMTSALES) AS FAMTSALES, SUM(FCSTSALES) AS FCSTSALES,
    SUM(FRETSALES) AS FRETSALES, SUM(NETSALES) AS NETSALES, SUM(MARAMOUNT) AS MARAMOUNT,
    SUM(YTDQTYSOLD) AS YTDQTYSOLD, SUM(YTDFAMTSALES) AS YTDFAMSALES,
    SUM(YTDFCSTSALES) AS YTDFCSTSALES, SUM(YTDFRETSALES) AS YTDFRETSALES,
    SUM(YTDNETSALES) AS YTDNETSALES, SUM(YTDGROSMARGIN) AS YTDGORSMARGIN
  FROM sales_history_details
  GROUP BY CUSTOMER, ITEM, YR, PERIOD, SALESPER, TERRITORY, CATEGORY, LOCATION
  SQL
  create_view "sales_analyses", sql_definition: <<-SQL
      SELECT
    IDCUST, NAMECUST, CODESLSP1, CODESLSP2, CODESLSP3,
    IDGRP, IDNATACCT, CODETERR, NAMECITY, CODESTTE, CODECTRY,
    ITEMNO, FMTITEMNO, [DESC], items_rankings.abc_sales_rank AS RANKING,
    OPTFCAT, OPTFDEP, OPTFPROD, OPTFCOL,
    OPTFTYPE, FAMILY, SUBFAMILY, CATEGORY, CATDESC, SALESPER,
    TERRITORY, LOCATION, COMMODIM, YR, PERIOD,
    SUM(QTYSOLD) AS QTYSOLD,
    SUM(FAMTSALES) AS FAMTSALES,
    SUM(FCSTSALES) AS FCSTSALES,
    SUM(FRETSALES) AS FRETSALES,
    SUM(NETSALES) AS NETSALES,
    SUM(MARAMOUNT) AS MARAMOUNT,
    SUM(YTDQTYSOLD) AS YTDQTYSOLD,
    SUM(YTDFAMTSALES) AS YTDFAMTSALES,
    SUM(YTDFCSTSALES) AS YTDFCSTSALES,
    SUM(YTDFRETSALES) AS YTDFRETSALES,
    SUM(YTDNETSALES) AS YTDNETSALES,
    SUM(YTDGROSMARGIN) AS YTDGROSMARGIN,
    VENDOR1, VENDOR2, VENDOR3, VENDOR4, VENDOR5, VENDOR6
  FROM sales_analysis_details
  LEFT JOIN items_rankings ON ITEMNO = items_rankings.item
  WHERE (YR >= YEAR(DATEADD(MONTH, - 36, GETDATE())))
  GROUP BY IDCUST, NAMECUST, CODESLSP1, CODESLSP2, CODESLSP3,
    IDGRP, IDNATACCT, CODETERR, NAMECITY, CODESTTE, CODECTRY,
    ITEMNO, FMTITEMNO, [DESC], OPTFCAT, OPTFDEP, OPTFPROD,
    OPTFTYPE, OPTFCOL, FAMILY, SUBFAMILY, CATEGORY, CATDESC, SALESPER,
    TERRITORY, LOCATION, COMMODIM, YR, PERIOD, items_rankings.abc_sales_rank,
    VENDOR1, VENDOR2, VENDOR3, VENDOR4, VENDOR5, VENDOR6
  ;
  SQL
  create_view "diff_iciloc_orders", sql_definition: <<-SQL
      SELECT
    dbo.ICILOC.ITEMNO, dbo.ICILOC.LOCATION, dbo.ICILOC.QTYSALORDR AS ORDERED_ICILOC,
    SUM(dbo.OEORDD.QTYORDERED) AS ORDERED_OE, dbo.ICILOC.QTYCOMMIT,
    SUM(dbo.OEORDD.QTYCOMMIT) AS ORDCOMMIT
  FROM dbo.ICILOC
  INNER JOIN dbo.OEORDD ON REPLACE(dbo.OEORDD.ITEM, '-', '') = dbo.ICILOC.ITEMNO
  INNER JOIN dbo.OEORDH ON dbo.OEORDD.ORDUNIQ = dbo.OEORDH.ORDUNIQ 
    AND dbo.ICILOC.LOCATION = dbo.OEORDD.LOCATION
    AND dbo.OEORDD.COMPLETE = 0
    AND dbo.OEORDH.TYPE = 1 AND dbo.OEORDH.ONHOLD = 0
  GROUP BY
    dbo.ICILOC.ITEMNO, dbo.ICILOC.LOCATION, dbo.ICILOC.QTYONORDER,
    dbo.ICILOC.QTYSALORDR, dbo.ICILOC.QTYONHAND, dbo.ICILOC.QTYCOMMIT
  HAVING (SUM(dbo.OEORDD.QTYORDERED) <> dbo.ICILOC.QTYSALORDR)
