  create_view "item_company_details", sql_definition: <<-SQL
      SELECT [ITEMNO], [FMTITEMNO], [DESC], [CATEGORY]
      ,[CDESC], [OPTFCAT], [OPTFDEP], [OPTFPROD]
      ,[OPTFTYPE], it.abc_sales_rank, it.dense_sales_rank
