  create_view "CustomersRanking", sql_definition: <<-SQL
          SELECT
        q.CUSTOMER,
        CASE
          WHEN SUM(SALES_AMOUNT) OVER (ORDER BY SALES_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(SALES_AMOUNT) OVER (),0) <= 0.7 THEN 'A'
          WHEN SUM(SALES_AMOUNT) OVER (ORDER BY SALES_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(SALES_AMOUNT) OVER (),0) <= 0.9 THEN 'B'
          ELSE 'C'
        END AS ABC_SALES_RANK,
        DENSE_RANK() OVER (PARTITION BY 1,2 ORDER BY SALES_AMOUNT DESC) AS DENSE_SALES_RANK,

        CASE
          WHEN SUM(MARGIN_AMOUNT) OVER (ORDER BY MARGIN_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(MARGIN_AMOUNT) OVER (),0) <= 0.7 THEN 'A'
          WHEN SUM(MARGIN_AMOUNT) OVER (ORDER BY MARGIN_AMOUNT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(MARGIN_AMOUNT) OVER (),0) <= 0.9 THEN 'B'
          ELSE 'C'
        END AS ABC_MARGIN_RANK,
        DENSE_RANK() OVER (PARTITION BY 1,2 ORDER BY MARGIN_AMOUNT DESC) AS DENSE_MARGIN_RANK,
        CASE
          WHEN SUM(MARGIN_PERCENT) OVER (ORDER BY MARGIN_PERCENT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(MARGIN_PERCENT) OVER (),0) <= 0.7 THEN 'A'
          WHEN SUM(MARGIN_PERCENT) OVER (ORDER BY MARGIN_PERCENT DESC ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) / NULLIF(SUM(MARGIN_PERCENT) OVER (),0) <= 0.9 THEN 'B'
          ELSE 'C'
        END AS ABC_PERCENT_RANK,
        DENSE_RANK() OVER (PARTITION BY 1,2 ORDER BY MARGIN_PERCENT DESC) AS DENSE_PERCENT_RANK
        FROM
          (SELECT
            RTRIM(CUSTOMER) AS CUSTOMER,
            SUM(FAMTSALES)-SUM(FRETSALES) AS SALES_AMOUNT,
            SUM(FCSTSALES) AS COST_AMOUNT,
            SUM(FAMTSALES)-SUM(FRETSALES)-SUM(FCSTSALES) AS MARGIN_AMOUNT,
            CASE
              WHEN SUM(FAMTSALES) = 0 OR SUM(FCSTSALES) = 0
              THEN 0
            ELSE ((SUM(FAMTSALES)-SUM(FRETSALES)-SUM(FCSTSALES))/SUM(FAMTSALES)*100)
            END AS MARGIN_PERCENT
          FROM OESHDT
          WHERE DATEADD(MONTH, -36, GETDATE()) <= CONVERT(DATE,NULLIF(CONVERT(VARCHAR(10),CONVERT(INT,TRANDATE)),0))
          GROUP BY CUSTOMER) q
  SQL
  create_view "UnPaidPayable", sql_definition: <<-SQL
      SELECT
          IDVEND AS VENDORID, VENDNAME, CODECTRY, IDINVC, IDORDERNBR, 
          CASE 
              WHEN d .[TXTTRXTYPE] = 1 THEN 'Invoice'
              WHEN d .[TXTTRXTYPE] = 2 THEN 'Debit Note'
              WHEN d .[TXTTRXTYPE] = 3 THEN 'Credit Note'
              WHEN d .[TXTTRXTYPE] = 4 THEN 'Interest'
              WHEN d .[TXTTRXTYPE] = 10 THEN 'Prepayment'
              WHEN d .[TXTTRXTYPE] = 11 THEN 'Payment'
              ELSE 'Unknown'
          END AS DOCTYPE, IDVENDGRP, DATEINVC, DATEINVCDU, DATEASOF, DATELSTACT, DATELSTSTM, CODETERM,
          CODECURN, AMTINVCHC, AMTDUEHC, ISNULL
              (
                  (SELECT TOP (1) SUM(PCTDUE) AS Expr1
                  FROM dbo.ARRTB AS t
                  WHERE (CODETERM = d.CODETERM) AND (DATEADD(DAY, DUEDAYS, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, d.DATEINVC)), 0))) < GETDATE())
                  GROUP BY CODETERM) * AMTINVCHC / 100, 0
              ) AS INAMTOVER, ISNULL
              (
                  (SELECT TOP (1) SUM(PCTDUE) AS Expr1
                  FROM dbo.ARRTB AS t
                  WHERE (CODETERM = d.CODETERM) AND (DATEADD(DAY, DUEDAYS, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, d.DATEINVC)), 0))) < GETDATE())
                  GROUP BY CODETERM), 0
              ) AS TERMPERTOVER, ISNULL
              (
                  (SELECT TOP (1) DATEDIFF(DAY, DATEADD(DAY, DUEDAYS, CONVERT(DATE, NULLIF (CONVERT(VARCHAR(10), CONVERT(INT, d.DATEINVC)), 0))), GETDATE()) AS Expr1
                  FROM dbo.ARRTB AS t
                  WHERE (CODETERM = d.CODETERM)
                  ORDER BY CODETERM, CNTPAYM), 0
              ) AS TERMDAYSOVER
      FROM dbo.APOBL AS d
      JOIN APVEN ON d.IDVEND = APVEN.VENDORID
      WHERE (SWPAID = 0)
  SQL
  create_view "PurchasesAnalysis", sql_definition: <<-SQL
      SELECT
      LTRIM(RTRIM(ph.VENDOR)) AS VENDOR,
      LTRIM(RTRIM(v.VENDNAME)) AS VENDNAME,
      LTRIM(RTRIM(v.CODECTRY)) AS COUNTRY,
      LTRIM(RTRIM(ph.ITEMNO)) AS ITEMNO,
      LTRIM(RTRIM(i.FMTITEMNO)) AS FMTITEMNO,
      LTRIM(RTRIM(i.[DESC])) AS ITEMDESC,
      LTRIM(RTRIM(i.CATEGORY)) AS CATEGORY,
      LTRIM(RTRIM(c.[DESC])) AS CATDESC,
      ph.FISCYEAR,
      ph.FISCPERIOD,
      
      -- Optional Fields --
      (
          SELECT LTRIM(RTRIM(VALUE))
          FROM ICITEMO AS o
          WHERE (ph.ITEMNO = o.ITEMNO) AND (OPTFIELD = 'CATEGORY')
      ) AS OPTFCAT,
      (
          SELECT LTRIM(RTRIM(VALUE))
          FROM ICITEMO AS o
          WHERE (ph.ITEMNO = ITEMNO) AND (OPTFIELD = 'PRODUCT')
      ) AS OPTFPROD,
      (
          SELECT LTRIM(RTRIM(VALUE))
          FROM ICITEMO AS o
          WHERE (ph.ITEMNO = ITEMNO) AND (OPTFIELD = 'TYPE')
      ) AS OPTFTYPE,
      (
          SELECT LTRIM(RTRIM(VALUE))
          FROM ICITEMO AS o
          WHERE (ph.ITEMNO = ITEMNO) AND (OPTFIELD = 'SUBFAMILY')
      ) AS SUBFAMILY,
      -- End Optinal Fields --
  
      SUM(ph.SQINVTOTAL) AS RQTOTAL,
      SUM(ph.SCINVTOTAL) AS SCTOTAL,
      SUM(ph.FCINVTOTAL) AS FCTOTAL,
      SUM(ph.FCINVTOTAL+ph.FCCRNTOTAL) AS NETPURCHASE,
      SUM(ph.FCCRNTOTAL) AS FCCRNTOTAL,
      SUM(ph.FCDEBTOTAL) AS SCDISCTOT
  
  FROM POHSTH ph
      LEFT JOIN ICITEM i ON ph.ITEMNO = i.ITEMNO
      LEFT JOIN ICCATG c ON i.CATEGORY = c.CATEGORY
      JOIN APVEN v ON v.VENDORID = ph.VENDOR
  WHERE (ph.FISCYEAR >= YEAR(DATEADD(MONTH, - 60, GETDATE())))
  AND v.IDGRP = 'PRO'
  GROUP BY
      ph.VENDOR,
      v.VENDNAME,
      v.CODECTRY,
      ph.ITEMNO,
      i.FMTITEMNO,
      i.[DESC],
      i.CATEGORY,
      c.[DESC],
      ph.FISCYEAR,
      ph.FISCPERIOD
  SQL
  create_view "open_purchase_orders", sql_definition: <<-SQL
      SELECT
      POPORH1.PORHSEQ, POPORL.PORLREV, POPORL.PORLSEQ, POPORH1.PONUMBER,
      POPORH1.VDCODE, POPORH1.VDNAME, POPORL.ITEMNO,
      POPORL.LOCATION, POPORH1.DATE, POPORL.OQORDERED, POPORL.OQRECEIVED,
      POPORL.OQOUTSTAND, proforma_invoice.quantity AS PRODUCTION, proforma_invoice.etd AS ETD,
      paking_list.quantity AS PACKED, paking_list.name AS DOCTYPE, paking_list.created_at AS PACKDATE,
      bl.eta AS ETA, bl.onboard AS ONBOARD, bl.container AS CONTAINER, POPORH2.ENTEREDBY,
      POPORL.UNITCOST, POPORL.EXTENDED
  FROM POPORL
  JOIN POPORH1 ON POPORL.PORHSEQ = POPORH1.PORHSEQ
  LEFT JOIN (
      SELECT 
          porhseq, itemno, quantity, documents.id, document_types.name,
          document_lines.created_at
          FROM [ACCLTD].[dbo].[document_lines]
          JOIN documents ON document_lines.document_id = documents.id
          JOIN document_types ON documents.document_type_id = document_types.id
          WHERE document_types.slug = 'packing-list'
  )paking_list ON POPORL.PORHSEQ = paking_list.porhseq AND POPORL.ITEMNO = paking_list.itemno COLLATE DATABASE_DEFAULT
  LEFT JOIN (
      SELECT 
          porhseq, itemno, quantity, documents.id, document_types.name,
          JSON_VALUE(properties_column, '$."Estimated Time of Delivery (ETD)"') AS etd, document_lines.created_at
          FROM [ACCLTD].[dbo].[document_lines]
          JOIN documents ON document_lines.document_id = documents.id
          JOIN document_types ON documents.document_type_id = document_types.id
          WHERE document_types.slug = 'proforma-invoice'
  )proforma_invoice ON POPORL.PORHSEQ = proforma_invoice.porhseq AND POPORL.ITEMNO = proforma_invoice.itemno COLLATE DATABASE_DEFAULT
  LEFT JOIN (
      SELECT 
          porhseq, itemno, quantity, documents.id, document_types.name,
          JSON_VALUE(properties_column, '$."On Board Date"') AS onboard,
          JSON_VALUE(properties_column, '$."Estimated Time of Arrival (ETA)"') AS eta,
          JSON_VALUE(properties_column, '$."Container Number"') AS container, 
          document_lines.created_at
          FROM [ACCLTD].[dbo].[document_lines]
          JOIN documents ON document_lines.document_id = documents.id
          JOIN document_types ON documents.document_type_id = document_types.id
          WHERE document_types.slug = 'bill-of-lading'
  )bl ON POPORL.PORHSEQ = bl.porhseq AND POPORL.ITEMNO = bl.itemno COLLATE DATABASE_DEFAULT
  JOIN POPORH2 ON POPORH1.PORHSEQ = POPORH2.PORHSEQ
  WHERE POPORL.COMPLETION = 1
  ;
  SQL
  create_view "open_orders", sql_definition: <<-SQL
      SELECT
    TOP (100) PERCENT dbo.OEORDD.ITEM, dbo.ICITEM.[DESC],
    dbo.OEORDH.ORDUNIQ, dbo.OEORDH.ORDNUMBER, dbo.OEORDH.LASTINVNUM,
    dbo.OEORDD.LOCATION, dbo.OEORDH.CUSTOMER, dbo.OEORDH.BILNAME AS CUSTNAME,
    dbo.OEORDH.SALESPER1 AS SALESREP, dbo.OEORDH.ORDDATE, dbo.OEORDD.QTYORDERED,
    dbo.OEORDD.QTYBACKORD, dbo.OEORDD.QTYCOMMIT, dbo.OEORDD.UNITPRICE * dbo.OEORDD.QTYBACKORD AS ORDAMOUNT,
    dbo.OEORDH.CUSTGROUP, dbo.OEORDD.MOSTREC * dbo.OEORDD.QTYBACKORD AS MRCAMOUNT, dbo.OEORDH1.ENTEREDBY,
    ICILOC_1.QTYONHAND - ICILOC_1.QTYCOMMIT AS LOCSTOCK, PONUMBER, REFERENCE,
    (
      SELECT SUM(QTYONHAND - QTYCOMMIT) AS Expr1
      FROM dbo.ICILOC
      WHERE (REPLACE(dbo.OEORDD.ITEM, '-', '') = ITEMNO)
    ) AS COMPSTOCK
  FROM dbo.OEORDH
  INNER JOIN dbo.OEORDH1 ON dbo.OEORDH1.ORDUNIQ = dbo.OEORDH.ORDUNIQ
  INNER JOIN dbo.OEORDD ON dbo.OEORDD.ORDUNIQ = dbo.OEORDH.ORDUNIQ
  INNER JOIN dbo.ICILOC AS ICILOC_1 ON REPLACE(dbo.OEORDD.ITEM, '-', '') = ICILOC_1.ITEMNO AND dbo.OEORDD.LOCATION = ICILOC_1.LOCATION
  INNER JOIN dbo.ICITEM ON REPLACE(dbo.OEORDD.ITEM, '-', '') = dbo.ICITEM.ITEMNO
  WHERE (dbo.OEORDH.TYPE <> 4)
    AND (dbo.OEORDD.COMPLETE = 0)
    AND (dbo.OEORDD.QTYBACKORD > 0)
  ORDER BY dbo.OEORDD.ITEM, dbo.OEORDD.LOCATION
  ;
  SQL
  create_view "order_actions", sql_definition: <<-SQL
      SELECT
    open_orders.ITEM, open_orders.[DESC], open_orders.ORDUNIQ, open_orders.ORDNUMBER, open_orders.LASTINVNUM,
    open_orders.LOCATION, open_orders.CUSTOMER, open_orders.CUSTNAME, open_orders.SALESREP,
    open_orders.ORDDATE, open_orders.QTYORDERED, open_orders.QTYCOMMIT, open_orders.ORDAMOUNT,
    open_orders.CUSTGROUP, open_orders.LOCSTOCK, open_orders.COMPSTOCK, order_metrics.BACKORDQTY,
    order_metrics.FULFILLQTY, order_metrics.OPENORDQTY, Granite.dbo.[Document].ShippingReference AS REFERENCE,
    open_orders.PONUMBER, open_orders.REFERENCE AS SOREFERENCE, open_orders.MRCAMOUNT, open_orders.ENTEREDBY,
        (
          SELECT id
          FROM dbo.shipping_references
          WHERE (number = Granite.dbo.[Document].ShippingReference COLLATE Latin1_General_BIN)
        ) AS REFID,
        (
          SELECT created_at
          FROM dbo.shipping_references AS shipping_references_2
          WHERE (number = Granite.dbo.[Document].ShippingReference COLLATE Latin1_General_BIN)
        ) AS REFDATE,
        (
          SELECT status
          FROM dbo.shipping_references AS shipping_references_1
          WHERE (number = Granite.dbo.[Document].ShippingReference COLLATE Latin1_General_BIN)
        ) AS REFSTATUS, dbo.ARCUS.CODECTRY, items_rankings.abc_sales_rank, items_rankings.dense_sales_rank, 
    items_rankings.abc_cost_rank, items_rankings.dense_cost_rank, items_rankings.abc_margin_rank,
    items_rankings.dense_margin_rank, item_optional_fields.OPTFCAT, item_optional_fields.OPTFDEPT, 
    item_optional_fields.OPTFPROD, item_optional_fields.OPTFTYPE, item_optional_fields.CATDESC,
    item_optional_fields.CATEGORY
  FROM  open_orders 
  INNER JOIN order_metrics ON open_orders.ITEM = order_metrics.ITEM
  INNER JOIN Granite.dbo.[Document] ON open_orders.ORDNUMBER = Granite.dbo.[Document].Number COLLATE Latin1_General_BIN
  INNER JOIN dbo.ARCUS ON open_orders.CUSTOMER = dbo.ARCUS.IDCUST
  INNER JOIN item_optional_fields ON open_orders.ITEM = item_optional_fields.FMTITEMNO
  LEFT OUTER JOIN items_rankings ON REPLACE(open_orders.ITEM, '-', '') = items_rankings.item
