  create_view "OrdersToFulfill", sql_definition: <<-SQL
      SELECT
          TOP (100) PERCENT dbo.OEORDD.ITEM, dbo.ICITEM.[DESC], dbo.OEORDH.ORDUNIQ, dbo.OEORDH.ORDNUMBER, dbo.OEORDD.LOCATION, 
          dbo.OEORDH.CUSTOMER, dbo.OEORDH.BILNAME AS CUSTNAME, dbo.OEORDH.SALESPER1 AS SALESREP, dbo.OEORDH.ORDDATE, dbo.OEORDD.QTYORDERED, 
          dbo.OEORDD.QTYBACKORD, dbo.OEORDD.QTYCOMMIT, dbo.ICILOC.QTYONHAND - dbo.ICILOC.QTYCOMMIT AS QTYAVAILABLE, 
          dbo.OEORDD.UNITPRICE * dbo.OEORDD.QTYBACKORD AS AMOUNT, dbo.OEORDH.CUSTGROUP
        FROM dbo.OEORDH 
        INNER JOIN dbo.OEORDD ON dbo.OEORDD.ORDUNIQ = dbo.OEORDH.ORDUNIQ
        INNER JOIN dbo.ICILOC ON REPLACE(dbo.OEORDD.ITEM, '-', '') = dbo.ICILOC.ITEMNO 
          AND dbo.OEORDD.QTYBACKORD < dbo.ICILOC.QTYONHAND - dbo.ICILOC.QTYCOMMIT 
          AND dbo.OEORDD.LOCATION = dbo.ICILOC.LOCATION 
        INNER JOIN dbo.ICITEM ON dbo.ICILOC.ITEMNO = dbo.ICITEM.ITEMNO
        WHERE     (dbo.OEORDH.TYPE <> 4) AND (dbo.OEORDD.QTYBACKORD - dbo.OEORDD.QTYCOMMIT > 0) AND (dbo.OEORDD.COMPLETE = 0)
        ORDER BY dbo.OEORDH.ORDDATE
  SQL
  create_view "OpenOrders", sql_definition: <<-SQL
      SELECT
          TOP (100) PERCENT dbo.OEORDD.ITEM, dbo.ICITEM.[DESC], dbo.OEORDH.ORDUNIQ, dbo.OEORDH.ORDNUMBER, dbo.OEORDD.LOCATION, 
          dbo.OEORDH.CUSTOMER, dbo.OEORDH.BILNAME AS CUSTNAME, dbo.OEORDH.SALESPER1 AS SALESREP, dbo.OEORDH.ORDDATE, dbo.OEORDD.QTYORDERED, 
          dbo.OEORDD.QTYBACKORD, dbo.OEORDD.QTYCOMMIT, dbo.OEORDD.UNITPRICE * dbo.OEORDD.QTYBACKORD AS ORDAMOUNT, dbo.OEORDH.CUSTGROUP,
          dbo.OEORDD.MOSTREC * dbo.OEORDD.QTYBACKORD AS MRCAMOUNT, dbo.OEORDH1.ENTEREDBY,
          ICILOC_1.QTYONHAND - ICILOC_1.QTYCOMMIT AS LOCSTOCK, PONUMBER, REFERENCE,
          (
            SELECT SUM(QTYONHAND - QTYCOMMIT) AS Expr1
            FROM dbo.ICILOC
            WHERE (REPLACE(dbo.OEORDD.ITEM, '-', '') = ITEMNO)
          ) AS COMPSTOCK
        FROM dbo.OEORDH
        INNER JOIN dbo.OEORDH1 ON dbo.OEORDH1.ORDUNIQ = dbo.OEORDH.ORDUNIQ
        INNER JOIN dbo.OEORDD ON dbo.OEORDD.ORDUNIQ = dbo.OEORDH.ORDUNIQ
        INNER JOIN dbo.ICILOC AS ICILOC_1 ON REPLACE(dbo.OEORDD.ITEM, '-', '') = ICILOC_1.ITEMNO AND dbo.OEORDD.LOCATION = ICILOC_1.LOCATION
        INNER JOIN dbo.ICITEM ON REPLACE(dbo.OEORDD.ITEM, '-', '') = dbo.ICITEM.ITEMNO
        WHERE (dbo.OEORDH.TYPE <> 4)
          AND (dbo.OEORDD.COMPLETE = 0)
          AND (dbo.OEORDD.QTYBACKORD > 0)
          --AND (dbo.OEORDH.LOCATION NOT IN ('2D', '9', '12'))
        ORDER BY dbo.OEORDD.ITEM, dbo.OEORDD.LOCATION
  SQL
  create_view "OpenPurchaseOrders", sql_definition: <<-SQL
      SELECT
          POPORH1.PORHSEQ, POPORL.PORLREV, POPORL.PORLSEQ, POPORH1.PONUMBER,
          POPORH1.VDCODE, POPORH1.VDNAME, POPORL.ITEMNO, paking_list.itemno,
          POPORL.LOCATION, POPORH1.DATE, POPORL.OQORDERED, POPORL.OQRECEIVED,
          POPORL.OQOUTSTAND, proforma_invoice.quantity AS PRODUCTION, proforma_invoice.etd AS ETD,
          paking_list.quantity AS PACKED, paking_list.name AS DOCTYPE, paking_list.created_at AS PACKDATE,
          bl.eta AS ETA, bl.onboard AS ONBOARD, bl.container AS CONTAINER, POPORH2.ENTEREDBY,
          POPORL.UNITCOST, POPORL.EXTENDED
      FROM POPORL
      JOIN POPORH1 ON POPORL.PORHSEQ = POPORH1.PORHSEQ
      LEFT JOIN (
          SELECT 
              porhseq, itemno, quantity, documents.id, document_types.name,
              document_lines.created_at
              FROM [ACCLTD].[dbo].[document_lines]
              JOIN documents ON document_lines.document_id = documents.id
              JOIN document_types ON documents.document_type_id = document_types.id
              WHERE document_types.slug = 'packing-list'
      )paking_list ON POPORL.PORHSEQ = paking_list.porhseq AND POPORL.ITEMNO = paking_list.itemno
      LEFT JOIN (
          SELECT 
              porhseq, itemno, quantity, documents.id, document_types.name,
              JSON_VALUE(properties_column, '$."Estimated Time of Delivery (ETD)"') AS etd, document_lines.created_at
              FROM [ACCLTD].[dbo].[document_lines]
              JOIN documents ON document_lines.document_id = documents.id
              JOIN document_types ON documents.document_type_id = document_types.id
              WHERE document_types.slug = 'proforma-invoice'
      )proforma_invoice ON POPORL.PORHSEQ = proforma_invoice.porhseq AND POPORL.ITEMNO = proforma_invoice.itemno
      LEFT JOIN (
          SELECT 
              porhseq, itemno, quantity, documents.id, document_types.name,
              JSON_VALUE(properties_column, '$."On Board Date"') AS onboard,
              JSON_VALUE(properties_column, '$."Estimated Time of Arrival (ETA)"') AS eta,
              JSON_VALUE(properties_column, '$."Container Number"') AS container, 
              document_lines.created_at
              FROM [ACCLTD].[dbo].[document_lines]
              JOIN documents ON document_lines.document_id = documents.id
              JOIN document_types ON documents.document_type_id = document_types.id
              WHERE document_types.slug = 'bill-of-lading'
      )bl ON POPORL.PORHSEQ = bl.porhseq AND POPORL.ITEMNO = bl.itemno
      JOIN POPORH2 ON POPORH1.PORHSEQ = POPORH2.PORHSEQ
      WHERE POPORL.COMPLETION = 1
  SQL
  create_view "ItemsMovingAveDetail", sql_definition: <<-SQL
