  create_view "OrdersMetric", sql_definition: <<-SQL
      SELECT ITEM, SUM(QTYORDERED) AS OPENORDQTY, COMPSTOCK AS STOCKQTY, SUM(QTYCOMMIT) AS COMMITQTY, 
          CASE 
            WHEN COMPSTOCK - SUM(QTYORDERED - QTYCOMMIT) < 0
            THEN (SUM(QTYORDERED - QTYCOMMIT)) - COMPSTOCK
            ELSE 0.00
          END AS BACKORDQTY, 

          CASE 
            WHEN (COMPSTOCK - SUM(QTYORDERED)) > 0
            THEN SUM(QTYORDERED)
            ELSE COMPSTOCK
          END AS FULFILLQTY
        FROM dbo.OpenOrders
        GROUP BY ITEM, COMPSTOCK
  SQL
  create_view "OrderAction", sql_definition: <<-SQL
      SELECT
          dbo.OpenOrders.ITEM, dbo.OpenOrders.[DESC], dbo.OpenOrders.ORDUNIQ, dbo.OpenOrders.ORDNUMBER,
          dbo.OpenOrders.LOCATION, dbo.OpenOrders.CUSTOMER, dbo.OpenOrders.CUSTNAME, dbo.OpenOrders.SALESREP,
          dbo.OpenOrders.ORDDATE, dbo.OpenOrders.QTYORDERED, dbo.OpenOrders.QTYCOMMIT, dbo.OpenOrders.ORDAMOUNT,
          dbo.OpenOrders.CUSTGROUP, dbo.OpenOrders.LOCSTOCK, dbo.OpenOrders.COMPSTOCK, dbo.OrdersMetric.BACKORDQTY,
          dbo.OrdersMetric.FULFILLQTY, dbo.OrdersMetric.OPENORDQTY, Granite.dbo.[Document].ShippingReference AS REFERENCE,
          dbo.OpenOrders.PONUMBER, dbo.OpenOrders.REFERENCE AS SOREFERENCE, dbo.OpenOrders.MRCAMOUNT, dbo.OpenOrders.ENTEREDBY,
              (
                SELECT id
                FROM dbo.shipping_references
                WHERE (number = Granite.dbo.[Document].ShippingReference COLLATE Latin1_General_BIN)
              ) AS REFID,
              (
                SELECT created_at
                FROM dbo.shipping_references AS shipping_references_2
                WHERE (number = Granite.dbo.[Document].ShippingReference COLLATE Latin1_General_BIN)
              ) AS REFDATE,
              (
                SELECT status
                FROM dbo.shipping_references AS shipping_references_1
                WHERE (number = Granite.dbo.[Document].ShippingReference COLLATE Latin1_General_BIN)
              ) AS REFSTATUS, dbo.ARCUS.CODECTRY, dbo.item_ranks.abc_sales_rank, dbo.item_ranks.dense_sales_rank, 
          dbo.item_ranks.abc_cost_rank, dbo.item_ranks.dense_cost_rank, dbo.item_ranks.abc_margin_rank,
          dbo.item_ranks.dense_margin_rank, dbo.ItemOptionalFields.OPTFCAT, dbo.ItemOptionalFields.OPTFDEPT, 
          dbo.ItemOptionalFields.OPTFPROD, dbo.ItemOptionalFields.OPTFTYPE, dbo.ItemOptionalFields.CATDESC,
          dbo.ItemOptionalFields.CATEGORY
        FROM  dbo.OpenOrders 
        INNER JOIN dbo.OrdersMetric ON dbo.OpenOrders.ITEM = dbo.OrdersMetric.ITEM
        INNER JOIN Granite.dbo.[Document] ON dbo.OpenOrders.ORDNUMBER = Granite.dbo.[Document].Number COLLATE Latin1_General_BIN
        INNER JOIN dbo.ARCUS ON dbo.OpenOrders.CUSTOMER = dbo.ARCUS.IDCUST
        INNER JOIN dbo.ItemOptionalFields ON dbo.OpenOrders.ITEM = dbo.ItemOptionalFields.FMTITEMNO
        LEFT OUTER JOIN dbo.item_ranks ON REPLACE(dbo.OpenOrders.ITEM, '-', '') = dbo.item_ranks.item
        GROUP BY dbo.OpenOrders.ITEM, dbo.OpenOrders.[DESC], dbo.OpenOrders.ORDUNIQ, dbo.OpenOrders.ORDNUMBER,
          dbo.OpenOrders.LOCATION, dbo.OpenOrders.CUSTOMER, dbo.OpenOrders.CUSTNAME, dbo.OpenOrders.ENTEREDBY,
          dbo.OpenOrders.SALESREP, dbo.OpenOrders.ORDDATE, dbo.OpenOrders.QTYORDERED,
          dbo.OpenOrders.QTYCOMMIT, dbo.OpenOrders.ORDAMOUNT, dbo.OpenOrders.CUSTGROUP,
          dbo.OpenOrders.LOCSTOCK, dbo.OpenOrders.COMPSTOCK, dbo.OrdersMetric.BACKORDQTY,
          dbo.OrdersMetric.FULFILLQTY, dbo.OrdersMetric.OPENORDQTY, Granite.dbo.[Document].ShippingReference,
          dbo.ARCUS.CODECTRY, dbo.item_ranks.abc_sales_rank, dbo.item_ranks.dense_sales_rank,
          dbo.item_ranks.abc_cost_rank, dbo.item_ranks.dense_cost_rank, dbo.item_ranks.abc_margin_rank,
          dbo.item_ranks.dense_margin_rank, dbo.ItemOptionalFields.OPTFCAT, dbo.ItemOptionalFields.OPTFDEPT,
          dbo.ItemOptionalFields.OPTFPROD, dbo.ItemOptionalFields.OPTFTYPE, dbo.ItemOptionalFields.CATDESC, 
          dbo.ItemOptionalFields.CATEGORY, dbo.OpenOrders.PONUMBER, dbo.OpenOrders.REFERENCE, dbo.OpenOrders.MRCAMOUNT
  SQL
  create_view "InventoryDiff", sql_definition: <<-SQL
      SELECT
          TOP (100) PERCENT dbo.ICILOC.ITEMNO, dbo.ICITEM.FMTITEMNO, dbo.ICITEM.[DESC], 
          dbo.ICILOC.LOCATION, dbo.ICILOC.COSTUNIT, dbo.ICILOC.QTYONHAND, dbo.ICILOC.QTYCOMMIT, 
          dbo.ICILOC.QTYONHAND - dbo.ICILOC.QTYCOMMIT AS AVAILABLE, 
          CASE 
              WHEN
              SUM(Granite.dbo.App_Inventory_ByLocation.Qty) IS NULL THEN 0.00
              ELSE SUM(Granite.dbo.App_Inventory_ByLocation.Qty)
          END AS QTYGRANIT,
          dbo.ICILOC.QTYSALORDR, dbo.ICILOC.QTYONORDER, dbo.ICILOC.LASTSHIPDT, dbo.ICILOC.LASTRCPTDT, dbo.ICILOC.LASTCOST,
          dbo.ItemsRanking.abc_sales_rank, dbo.ItemsRanking.dense_sales_rank,
          (
                  SELECT LTRIM(RTRIM(VALUE))
                  FROM dbo.ICITEMO AS o
                  WHERE (ICILOC.ITEMNO = ITEMNO) AND (OPTFIELD = 'DEPT')
          ) AS OPTFDEP
        FROM dbo.ICILOC
        FULL OUTER JOIN
          Granite.dbo.App_Inventory_ByLocation ON dbo.ICILOC.ITEMNO COLLATE DATABASE_DEFAULT = Granite.dbo.App_Inventory_ByLocation.Code COLLATE DATABASE_DEFAULT AND  
          dbo.ICILOC.LOCATION COLLATE DATABASE_DEFAULT = Granite.dbo.App_Inventory_ByLocation.ERPLocation COLLATE DATABASE_DEFAULT
        FULL OUTER JOIN dbo.ICITEM ON dbo.ICILOC.ITEMNO = dbo.ICITEM.ITEMNO
        FULL OUTER JOIN dbo.ItemsRanking ON dbo.ICILOC.ITEMNO = dbo.ItemsRanking.item
        WHERE dbo.ICILOC.LOCATION IS NOT NULL
        GROUP BY
          dbo.ICILOC.ITEMNO, dbo.ICILOC.LOCATION, dbo.ICILOC.QTYONHAND, dbo.ICILOC.QTYCOMMIT, dbo.ICILOC.QTYSALORDR, 
          dbo.ICILOC.QTYONORDER, dbo.ICILOC.DAYSTOSHIP, dbo.ICILOC.LASTSHIPDT, dbo.ICILOC.LASTRCPTDT, dbo.ICILOC.LASTCOST,
          dbo.ICITEM.[DESC], dbo.ICILOC.COSTUNIT, dbo.ItemsRanking.abc_sales_rank, dbo.ItemsRanking.dense_sales_rank, 
          dbo.ICITEM.FMTITEMNO
        ORDER BY dbo.ICILOC.ITEMNO
  SQL
  create_view "LocationDiff", sql_definition: <<-SQL
      SELECT
          i.FMTITEMNO, i.[DESC], l.ITEMNO, l.LOCATION, l.QTYSALORDR,
          l.QTYCOMMIT, l.AUDTDATE,
          (
            SELECT ISNULL(SUM(od.QTYORDERED), 0.0000)
            FROM OEORDD od
            JOIN OEORDH oh ON od.ORDUNIQ = oh.ORDUNIQ
            WHERE od.LOCATION = l.LOCATION AND od.ITEM = i.FMTITEMNO
            AND od.COMPLETE = 0
            AND oh.TYPE = 1
            AND oh.ONHOLD = 0
          ) ORDEREDOE,
          (
            SELECT ISNULL(SUM(od.QTYCOMMIT), 0.0000)
            FROM OEORDD od
            JOIN OEORDH oh ON od.ORDUNIQ = oh.ORDUNIQ
            WHERE od.LOCATION = l.LOCATION AND od.ITEM = i.FMTITEMNO
            AND od.COMPLETE = 0
            AND oh.TYPE = 1
            AND oh.ONHOLD = 0
          ) AS ORDCOMMIT
        FROM dbo.ICILOC l
        LEFT JOIN dbo.ICITEM i ON l.ITEMNO = i.ITEMNO
        GROUP BY
          l.ITEMNO, i.ITEMNO, i.FMTITEMNO, i.[DESC], l.LOCATION,
          l.QTYONORDER, l.QTYSALORDR, l.QTYONHAND, l.QTYCOMMIT, l.AUDTDATE
  SQL
  create_view "FiscalSet", sql_definition: <<-SQL
      SELECT ACCTID, FSCSYR,
        AMOUNT, CURNTYPE, FSCSDSG,
        CASE FSPERD
          WHEN 'NETPERD1' THEN '01'
          WHEN 'NETPERD2' THEN '02'
          WHEN 'NETPERD3' THEN '03'
          WHEN 'NETPERD4' THEN '04'
          WHEN 'NETPERD5' THEN '05'
          WHEN 'NETPERD6' THEN '06'
          WHEN 'NETPERD7' THEN '07'
          WHEN 'NETPERD8' THEN '08'
          WHEN 'NETPERD9' THEN '09'
          WHEN 'NETPERD10' THEN '10'
          WHEN 'NETPERD11' THEN '11'
          WHEN 'NETPERD12' THEN '12'
        END AS FSPERD
        FROM GLAFS
        UNPIVOT (
          AMOUNT
          FOR FSPERD IN (
            NETPERD1,NETPERD2,NETPERD3,NETPERD4,
            NETPERD5,NETPERD6,NETPERD7,NETPERD8,
            NETPERD9,NETPERD10,NETPERD11,NETPERD12
          )  
        ) AS FSPERD
  SQL
  create_view "GeneralLedgerSummary", sql_definition: <<-SQL
      SELECT g.ACCTGRPCOD, g.ACCTGRPDES, g.GRPCOD, a.ACCTID,
          a.ACCTFMTTD, a.ACCTDESC,fs.FSCSYR, fs.FSPERD,
          SUM(fs.AMOUNT) AS AMOUNT,
          CASE
            WHEN SUM(fs.AMOUNT) < 0 THEN SUM(fs.AMOUNT)*-1
            ELSE SUM(fs.AMOUNT)
          END AS RAMOUNT,
          EOMONTH(CONCAT(FSCSYR,FSPERD)+'01') AS DATE,
          CASE a.ACCTTYPE
            WHEN 'I' THEN 'Income Statement'
            WHEN 'B' THEN 'Balance Sheet'
            WHEN 'R' THEN 'Retained Earnings'
          END AS ACCTTYPE,
          CASE fs.CURNTYPE
            WHEN 'S' THEN 'Source'
            WHEN 'E' THEN 'Equivalent'
            WHEN 'F' THEN 'Functional'
          END AS CURNTYPE
        FROM GLACGRP g
        JOIN GLAMF a ON g.ACCTGRPCOD = a.ACCTGRPCOD
        JOIN FiscalSet fs ON a.ACCTID = fs.ACCTID
        WHERE FSCSDSG = 'A'
        GROUP BY g.ACCTGRPCOD, g.ACCTGRPDES, g.GRPCOD, a.ACCTID,
          a.ACCTFMTTD, a.ACCTDESC, a.ACCTTYPE, fs.FSCSYR, fs.FSPERD,
          fs.CURNTYPE
  SQL
  create_view "CashFlowSummary", sql_definition: <<-SQL
      SELECT cr.CNTYR, cr.CNTPERD, EOMONTH(CONCAT(CNTYR,CNTPERD)+'01') AS DATE, SUM((cr.AMTPAYMHC)*-1) AS RECEIPTS,
          (
            SELECT SUM(AMTPAYMHC*-1)
            FROM APVSM
            WHERE CNTYR = cr.CNTYR
            AND CNTPERD = cr.CNTPERD
          ) AS PAYMENTS,
          (
            SELECT SUM(AMOUNT)
            FROM GeneralLedgerSummary
            WHERE FSCSYR = cr.CNTYR
            AND FSPERD = cr.CNTPERD
            AND ACCTGRPCOD = '1700'
          ) AS SALARIES,
          (
            SELECT SUM(AMOUNT)
            FROM GeneralLedgerSummary
            WHERE FSCSYR = cr.CNTYR
            AND FSPERD = cr.CNTPERD
            AND GRPCOD = 200
          ) AS INTERESTS,
          (
            SUM((cr.AMTPAYMHC)*-1) - 
            (SELECT SUM(AMTPAYMHC*-1) FROM APVSM WHERE CNTYR = cr.CNTYR AND CNTPERD = cr.CNTPERD) - 
            (SELECT SUM(AMOUNT) FROM GeneralLedgerSummary WHERE FSCSYR = cr.CNTYR AND FSPERD = cr.CNTPERD AND ACCTGRPCOD = '1700') -
            (SELECT SUM(AMOUNT) FROM GeneralLedgerSummary WHERE FSCSYR = cr.CNTYR AND FSPERD = cr.CNTPERD AND GRPCOD = 200)
          ) AS OPERCASH
        FROM ARCSM cr
        GROUP BY cr.CNTYR, cr.CNTPERD
  SQL
end
